var color = flow.get('colordropdownSelect')||"";
var beerArray = msg.payload.split(",");
if (beerArray[1] === undefined){
    beerArray[1] = true;
    flow.set(color + "-URL","");
}
flow.set(color + "-Beer",beerArray);
//node.warn(beerArray);

if (beerArray[1] === true){
    msg.payload = beerArray[0];
    msg.topic = "Beer name set for " + color;
} else {
    msg.payload = beerArray[0] + "," + beerArray[1];
    msg.topic = "[Beer name],[cloud ID] set for " + color;
}
return msg;

//get color-mac combo
var IDbyMAC = flow.get('IDbyMAC')||false;
if (IDbyMAC) {
msg.payload.Color = msg.payload.Color + ":" + msg.payload.mac;
msg.payload.displayColor = msg.payload.Color.split(":");
msg.payload.IDbyMAC = true;
} else {
    msg.payload.displayColor = [msg.payload.Color];
    msg.payload.IDbyMAC = false;
}
//set beer name and local name status
var beerArray = flow.get(msg.payload.Color + "-Beer")||["Untitled",true];
msg.payload.Beer = beerArray;
//set dropdown options
var options = flow.get('options')||[];
var color = msg.payload.Color;
if (options.indexOf(color) === -1){ //check if Tilt color has been seen
    options.push(color);
    options.sort();
    //node.warn(options);
    flow.set('options',options);
    node.send([null, {'payload': options}]);
}
//set SG cal points
var actualSGArray = flow.get('actualSGpoints-' + msg.payload.Color)||[];
var uncalSGArray = flow.get('uncalSGpoints-' + msg.payload.Color)||[];
msg.payload.actualSGPoints = actualSGArray.toString();
msg.payload.unCalSGPoints = uncalSGArray.toString();
//set Temp cal points
var actualTempArray = flow.get('actualTemppoints-' + msg.payload.Color)||[];
var uncalTempArray = flow.get('uncalTemppoints-' + msg.payload.Color)||[];
msg.payload.actualTempPoints = actualTempArray.toString();
msg.payload.unCalTempPoints = uncalTempArray.toString();
//add timeStamp parameter
msg.payload.timeStamp = Date.now();
var date = new Date();
//add Timepoint parameter
var timeZone = date.getTimezoneOffset()/24/60;
msg.payload.formatteddate = date.toLocaleString();
msg.payload.Timepoint = msg.payload.timeStamp / 1000 / 60 / 60 / 24 + 25569 - timeZone;
//add SG parameter, account for extra precision if needed
msg.payload.fermunits = flow.get('fermdisplayUnits')||'';
if (msg.payload.minor > 2000){
msg.payload.SG = msg.payload.minor / 10000;
msg.payload.major /= 10;
msg.payload.hd = true;
}else{
msg.payload.SG = msg.payload.minor / 1000;
msg.payload.hd = false;
}
//add Temp parameter
msg.payload.Temp = msg.payload.major;
msg.payload.tempunits = flow.get('displayUnits')||"°F";
//Google Sheet URL for displaying link in display
msg.payload.doclongurl = flow.get(msg.payload.Color + '-URL')||"";
msg.topic = msg.payload.Color;
msg.filename = "/home/pi/" + msg.payload.Color + ".json";
//get custom cloud URL if default cloud URL set to undefined
msg.payload.customcloudURL = flow.get('cloudURL-' + msg.payload.Color);
//set global cloud settings
msg.payload.defaultcloudURL = flow.get('cloudURL')||['https://script.google.com/macros/s/AKfycbwNXh6rEWoULd0vxWxDylG_PJwQwe0dn5hdtSkuC4k3D9AXBSA/exec',true];
msg.payload.logCloudDataCheck = flow.get('logCloudDataCheck')||true;
msg.payload.logLocalDataCheck = flow.get('logLocalDataCheck');
msg.payload.localloggingInterval = flow.get('localloggingInterval')||15;
msg.payload.loggingInterval = flow.get('loggingInterval')||15;
msg.payload.minRSSI = flow.get('minRSSI');
msg.payload.alphaSG = flow.get('alphaSG');
msg.payload.alphaTemp = flow.get('alphaTemp');
msg.payload.numberSamples = flow.get('numberSamples');
msg.payload.smoothSwitch = flow.get('smoothSwitch');
return [ msg, null ];

//this function block requires msg.payload.SG, msg.payload.Temp, msg.payload.actualSGPoints, msg.payload.actualTempPoints(a comma separated string of the actual values), msg.payload.unCalSGPoints, and msg.payload.unCalTempPoints (a comma separated string of the measured raw values)
function linearInterpolation (x, x0, y0, x1, y1) {
  var a = (y1 - y0) / (x1 - x0);
  var b = -a * x0 + y0;
  return a * x + b;
}
//calculate calibrated SG by linear interpolation of calibration points
var actualSGPoints = [];
var unCalSGPoints = [];
var unCalSGPointsTemp = [];
var SG = msg.payload.SG;
actualSGPoints = msg.payload.actualSGPoints.split(",");
unCalSGPoints = msg.payload.unCalSGPoints.split(",");
unCalSGPointsTemp = msg.payload.unCalSGPoints.split(",");
//ensure uncal value always between two numbers
unCalSGPointsTemp.push(-0.001, SG, Number.MAX_VALUE);
unCalSGPoints.push(-0.001, Number.MAX_VALUE);
actualSGPoints.push(-0.001, Number.MAX_VALUE);
unCalSGPointsTemp.sort(function(a, b){return a-b;});
unCalSGPoints.sort(function(a, b){return a-b;});
actualSGPoints.sort(function(a, b){return a-b;});
var indexSG = unCalSGPointsTemp.indexOf(SG);
var calSG = linearInterpolation (Number(SG), Number(unCalSGPoints[indexSG-1]), Number(actualSGPoints[indexSG-1]), Number(unCalSGPoints[indexSG]), Number(actualSGPoints[indexSG]));
msg.payload.uncalSG = SG;
msg.payload.uncalPlato = 1111.14 * msg.payload.uncalSG - 630.272 * msg.payload.uncalSG * msg.payload.uncalSG + 135.997 * msg.payload.uncalSG * msg.payload.uncalSG * msg.payload.uncalSG - 616.868;
msg.payload.uncalBrix = (((182.4601 * msg.payload.uncalSG - 775.6821) * msg.payload.uncalSG + 1262.7794) * msg.payload.uncalSG - 669.5622);
msg.payload.SG = calSG;
msg.payload.Plato = 1111.14 * msg.payload.SG - 630.272 * msg.payload.SG * msg.payload.SG + 135.997 * msg.payload.SG * msg.payload.SG * msg.payload.SG - 616.868;
msg.payload.Brix = (((182.4601 * msg.payload.SG - 775.6821) * msg.payload.SG + 1262.7794) * msg.payload.SG - 669.5622);
switch (msg.payload.fermunits){
    case "" :   if (msg.payload.hd){msg.payload.ferm = msg.payload.SG.toFixed(4);
                                    msg.payload.uncalferm = msg.payload.uncalSG.toFixed(4);
    }
                if (!msg.payload.hd){msg.payload.ferm = msg.payload.SG.toFixed(3);
                                     msg.payload.uncalferm = msg.payload.uncalSG.toFixed(3);
                }
    break;
    case "°P" : if (msg.payload.hd){msg.payload.ferm = msg.payload.Plato.toFixed(2);
                                   msg.payload.uncalferm = msg.payload.uncalPlato.toFixed(2);    
                }
                if (!msg.payload.hd){msg.payload.ferm = msg.payload.Plato.toFixed(1);
                                   msg.payload.uncalferm = msg.payload.uncalPlato.toFixed(1); 
                }
    break;
    case "°Bx": if (msg.payload.hd){msg.payload.ferm = msg.payload.Brix.toFixed(2);
                                   msg.payload.uncalferm = msg.payload.uncalBrix.toFixed(2);
                }
                if (!msg.payload.hd){msg.payload.ferm = msg.payload.Brix.toFixed(1);
                    msg.payload.uncalferm = msg.payload.uncalBrix.toFixed(1);
                }
}

//calculate calibrated Temp by linear interpolation of calibration points
var actualTempPoints = [];
var unCalTempPoints = [];
var unCalTempPointsTemp = [];
var Temp = msg.payload.Temp;
if (msg.payload.tempunits === "°C"){
    Temp -= 32;
    Temp *= 0.5555;
}
actualTempPoints = msg.payload.actualTempPoints.split(",");
unCalTempPoints = msg.payload.unCalTempPoints.split(",");
unCalTempPointsTemp = msg.payload.unCalTempPoints.split(",");
//ensure uncal value always between two numbers
unCalTempPointsTemp.push(-999, Temp, Number.MAX_VALUE);
unCalTempPoints.push(-999, Number.MAX_VALUE);
actualTempPoints.push(-999, Number.MAX_VALUE);
unCalTempPointsTemp.sort(function(a, b){return a-b;});
unCalTempPoints.sort(function(a, b){return a-b;});
actualTempPoints.sort(function(a, b){return a-b;});
//node.warn(actualTempPoints);
var indexTemp = unCalTempPointsTemp.indexOf(Temp);
var calTemp = linearInterpolation (Number(Temp), Number(unCalTempPoints[indexTemp-1]), Number(actualTempPoints[indexTemp-1]), Number(unCalTempPoints[indexTemp]), Number(actualTempPoints[indexTemp]));
if (Number.isNaN(calTemp)){
    calTemp = 0;
}
msg.payload.uncalTemp = Temp.toFixed(1);
msg.payload.displayuncalTemp = Temp.toFixed(1);
msg.payload.displayTemp = calTemp.toFixed(1);
if (msg.payload.tempunits === "°C"){
    calTemp *= 1.8;
    calTemp += 32;
    msg.payload.Temp = calTemp.toFixed(1);
}else{
msg.payload.Temp = calTemp.toFixed(1);
}
return msg;

var color = flow.get('colordropdownSelect')||undefined;
if (color !== undefined){
    var beerArray = flow.get(color + "-Beer")||["",true];
    if (beerArray[1] === true){
        msg.payload = beerArray[0];
    }
    else {
        msg.payload = beerArray;
    }
} else {
    msg.enabled = false;
}
return msg;

var color = flow.get('colordropdownSelect')||"";
var comment = msg.payload;
flow.set(color + "-Comment",comment);
flow.set('lastpost-' + color,0);
flow.set('lastlocalpost-' + color,0);
msg.payload = "Sending: " + comment;
msg.topic = "TILT | " + color;
return msg;

var postEnabled = (flow.get('logCloudDataCheck'))||false;
var interval = flow.get('loggingInterval')||15;
interval *= 60000;
if (postEnabled && msg.payload.Color !== undefined){
    var lastPost = flow.get('lastpost-' + msg.payload.Color)||0;
    if (msg.payload.timeStamp - lastPost > interval){
        flow.set('lastpost-' + msg.payload.Color,msg.payload.timeStamp);
        //node.warn(msg.payload.Color + ' triggered');
        //node.warn(msg.payload.timeStamp - lastPost);
        msg.payload.Comment = flow.get(msg.payload.Color + "-Comment")||"";
        msg.payload.Beer = flow.get(msg.payload.Color + "-Beer")||["Untitled",true];
        msg.topic = msg.payload.Color;
        if (msg.payload.Beer[1] === true){
            msg.payload.Beer = msg.payload.Beer[0];
        }
        //msg.headers = {};
        //msg.headers['content-type'] = 'application/x-www-form-urlencoded; charset=utf-8';
        var payloadEncoded = encodeURI("Timepoint=" + msg.payload.Timepoint + "&Temp=" + msg.payload.Temp + "&SG=" + msg.payload.SG + "&Beer=" + msg.payload.Beer + "&Color=" + msg.payload.Color + "&Comment=" + msg.payload.Comment);
        if (flow.get('cloudURL')[1] === true){
            var defaultCloudURL = flow.get('cloudURL')[0];
            node.send({ headers : { 'content-type' : 'application/x-www-form-urlencoded; charset=utf-8' }, url : defaultCloudURL, payload : payloadEncoded, cloudwait : 'Contacting Default Cloud URL...<br>(allow up to 30 seconds)' });
        }
        else{
            var cloudURLs = flow.get('cloudURL-' + msg.payload.Color)||'none';
            if (cloudURLs != 'none'){
             var cloudURLsArray = cloudURLs.split(',');
             for (var i = 0; i < cloudURLsArray.length; i++){
                node.send({ headers : { 'content-type' : 'application/x-www-form-urlencoded; charset=utf-8' }, url : cloudURLsArray[i], payload : payloadEncoded, cloudwait : 'Contacting Cloud URL ' + (i + 1) + '...<br>(allow up to 30 seconds)' });
              }
             }
            }
}
}

