var customReleases = flow.get('custom-update-release-url') + "/latest";

if (flow.get('dev-mode') && customReleases) {
    msg.url = customReleases;
} else {
    msg.url = defaultReleases;
}

msg.headers = {
    "User-Agent": "node-red;tiltpi"
}

return msg;

var status = msg.statusCode;
var resp = msg.payload;

var msg = {};
if (status == 200 && resp !== []) {
    var latest = flow.get("version");
    if (resp.name == latest.name) {
        msg.enabled = false;
        msg.label = "";
        msg.payload = "Running most current version.";
    } else {
      msg.enabled = true;
      msg.label = "Latest Update: " + resp.name;
      msg.payload = resp.body;
    }
} else {
    msg.enabled = false;
    msg.label = "Lastest Update: Unknown";
    msg.payload = "Failed to retrieve latest release information. Check internet connectivity."
}

return msg;

var release = {
    name: "v.2.8",
    notes: "Improves BLE scan reliability."
}
flow.set("version", release);

msg.label = release.name;
msg.payload = release.notes;
return msg;

