var smoothSwitch = flow.get('smoothSwitch')||false;
if (smoothSwitch && msg.payload.SG > 0.5){
var color = msg.payload.Color;
var SG = msg.payload.SG;
var Temp = msg.payload.Temp;
var alphaSG = flow.get('alphaSG') / 100||0.5;//ratio of current sample vs. average of previous samples over sample rate
var alphaTemp = flow.get('alphaTemp') / 100||0.5;//ratio of current sample vs. average of previous samples over sample rate
var numberSamples = flow.get('numberSamples') * 12 || 36;//multiply this number by sample rate to determine timeframe of moving average
var sampleRate = 5000;//measurements on Tilt taken every 5 seconds
var timeSinceLastSample = context.get(color + '-timeSinceLastSample')||sampleRate;
var prevSampleTime = context.get(color + '-prevSampleTime')||msg.payload.timeStamp;
var sampleTakenFlag = context.get(color + '-sampleTakenFlag')||"not taken";
var prevSGArray = context.get(color + '-SG')||[];
var prevTempArray = context.get(color + '-Temp')||[];
if (timeSinceLastSample >= sampleRate && sampleTakenFlag == "not taken"){//take sample if time elapsed is more than sample rate
 context.set(color + '-sampleTakenFlag', "taken");
 prevSGArray.push(SG);
 prevTempArray.push(Temp);
 if (prevSGArray.length > numberSamples || prevTempArray.length > numberSamples){
     prevSGArray.shift();
     prevTempArray.shift();
 }
 context.set(color + '-SG', prevSGArray);
 context.set(color + '-Temp', prevTempArray);
 context.set(color + '-prevSampleTime', msg.payload.timeStamp);
}else{
     context.set(color + '-sampleTakenFlag', "not taken")
}
timeSinceLastSample = msg.payload.timeStamp - prevSampleTime;
context.set(color + '-timeSinceLastSample', timeSinceLastSample)
//calculate means
var totalSG = 0;
for(var i = 0; i < prevSGArray.length; i++) {
    totalSG += prevSGArray[i];
}
var meanSG = totalSG / prevSGArray.length;
msg.payload.SG = ((SG * (1 - alphaSG)) + (meanSG * alphaSG));
var totalTemp = 0;
for(var i = 0; i < prevTempArray.length; i++) {
    totalTemp += prevTempArray[i];
}
var meanTemp = totalTemp / prevTempArray.length;
msg.payload.Temp = ((Temp * (1 - alphaTemp)) + (meanTemp * alphaTemp));
}
return msg;

